package main

import (
	"fmt"
)

func longestNiceSubarray(nums []int) int {
	var arr [][]int
	var cur []int
	for i := 0; i < len(nums); i++ {
		res := nums[i]
		cur = []int{nums[i]}
		for j := i + 1; j < len(nums); j++ {
			if res&nums[j] != 0 {
				break
			}
			cur = append(cur, nums[j])
			temp := make([]int, len(cur))
			copy(temp, cur)
			arr = append(arr, temp)
		}
	}
	//fmt.Printf("len: %d\n", len(arr))
	var ans int
	for i := 0; i < len(arr); i++ {
		flag := false
		for j := 0; j < len(arr[i]); j++ {
			for k := j + 1; k < len(arr[i]); k++ {
				if arr[i][j]&arr[i][k] != 0 {
					flag = true
					break
				}
			}
			if flag {
				break
			}
		}
		if !flag && len(arr[i]) > ans {
			ans = len(arr[i])
		}
	}
	if ans == 0 {
		return 1
	}
	return ans
}

